var xm = require('./');

var json = xm.load('<!-- comment break stuff --><svg id="test"><path d="M0,0L10,10"/><path d="M0,0L10,10"/></svg>');
var xml  = xm.dump(json);

console.log("\nWith comments\n");
console.log(xml,json);




json = xm.load('<!-- comment break stuff --><svg id="test"><path d="M0,0L10,10"/><path d="M0,0L10,10"/></svg>', {comments: false});
xml  = xm.dump(json);

console.log("\nWithout comments : \n");
console.log(xml,json);
